mapper.select:
SELECT GS.GOODS_NO, GS.GOODS_CATEGORY, GS.GOODS_NAME, GS.GOODS_CONTENT, GS.GOODS_ORIGIN_PRICE, GS.GOODS_SELL_PRICE, GS.GOODS_SALE_PRICE, GS.GOODS_DATE, GS.GOODS_KEYWORD, GS.GOODS_READCNT, GS.GOODS_PICK, GS.GOODS_THUMBNAIL, GS.GUBUN, GI.GOODS_IMAGE_STD FROM GOODS GS, GOODS_IMAGE GI WHERE GS.GOODS_NO = GI.GOODS_NO AND GOODS_CATEGORY = #{cate}
AND (GOODS_NAME LIKE '%' || #{keyword} || '%' OR GOODS_KEYWORD LIKE '%' || #{keyword} || '%')
AND GUBUN = '0'
ORDER BY ${orderBy} ${orderSort}

mapper.update:


UPDATE  GOODS
SET     GUBUN = #{GUBUN}
WHERE   GOODS_NO = #{IDX}



UPDATE  GOODS 
SET     GOODS_THUMBNAIL = #{GOODS_THUMBNAIL}
WHERE   GOODS_NO = #{IDX}

mapper.delete:


DELETE 
FROM    GOODS_LIKE 
WHERE   MEMBER_NO = #{MEMBER_NO}
AND     GOODS_NO = #{GOODS_NO}

mapper.update:

UPDATE  GOODS_ATTRIBUTE 
SET     GOODS_ATT_AMOUNT = GOODS_ATT_AMOUNT-#{ORDER_DETAIL_AMOUNT}
WHERE   GOODS_ATT_NO = #{GOODS_ATT_NO}



UPDATE  REVIEW
  SET   REVIEW_TITLE = #{REVIEW_TITLE},
        REVIEW_CONTENT = #{REVIEW_CONTENT},
        REVIEW_DATE = #{REVIEW_DATE}
WHERE   GOODS_NO = #{IDX}
  AND   REVIEW_NO = #{REVIEW_NO}
  AND   MEMBER_NO = #{MEMBER_NO}



UPDATE  REVIEW
  SET   REVIEW_TITLE = #{REVIEW_TITLE},
        REVIEW_CONTENT = #{REVIEW_CONTENT},
        REVIEW_DATE = #{REVIEW_DATE}
WHERE   GOODS_NO = #{IDX}
  AND   REVIEW_NO = #{REVIEW_NO}
  AND   MEMBER_NO = #{MEMBER_NO}
