mapper.select:
SELECT GOODS_NO, GOODS_CATEGORY, GOODS_NAME, GOODS_CONTENT, GOODS_ORIGIN_PRICE, GOODS_SELL_PRICE, GOODS_SALE_PRICE, GOODS_DATE, GOODS_KEYWORD, GOODS_READCNT, GOODS_PICK, GOODS_THUMBNAIL FROM GOODS GS WHERE GOODS_CATEGORY = #{cate}
ORDER BY ${orderBy} ${orderSort}
AND GOODS_NAME LIKE '%' || #{keyword} || '%'

mapper.update:

UPDATE  GOODS 
SET     GOODS_THUMBNAIL = #{GOODS_THUMBNAIL}
WHERE   GOODS_NO = #{IDX}

mapper.delete:

DELETE 
FROM    GOODS_LIKE 
WHERE   MEMBER_NO = #{MEMBER_NO}
AND     GOODS_NO = #{GOODS_NO}

mapper.update:

UPDATE  GOODS_ATTRIBUTE 
SET     GOODS_ATT_AMOUNT = GOODS_ATT_AMOUNT-#{ORDER_DETAIL_AMOUNT}
WHERE   GOODS_ATT_NO = #{GOODS_ATT_NO}
