mapper.update:

UPDATE  GOODS 
SET     GOODS_THUMBNAIL = (
                    SELECT  GOODS_IMAGE_STD
                    FROM    (
                            SELECT  GOODS_IMAGE_STD 
                            FROM    GOODS_IMAGE 
                            WHERE   GOODS_NO = #{IDX}
                           			 AND GOODS_IMAGE_DEL_GB = 'N'
                            ORDER BY GOODS_IMAGE_NUM ASC
                            )
                    WHERE   ROWNUM <= 1
                    )
WHERE   GOODS_NO = #{IDX}


UPDATE  GOODS_ATTRIBUTE 
SET     GOODS_ATT_AMOUNT = GOODS_ATT_AMOUNT-#{ORDER_DETAIL_AMOUNT}
WHERE   GOODS_ATT_NO = #{GOODS_ATT_NO}
