mapper.select:


UPDATE order_list
SET	order_state = order_state + 1
WHERE order_no = ${order_no}

mapper.update:



UPDATE order_list
SET order_state = '98'
where order_no = ${order_no}

mapper.select:

select goods_att_no, order_detail_amount 
from order_detail 
where order_no = ${order_no}

mapper.update:

update goods_attribute
set
    goods_att_amount = goods_att_amount + ${order_detail_amount}
where goods_att_no = ${goods_att_no}

mapper.select:


select member_no, order_state, order_no
from order_list 
where order_no = ${order_no}



select member_passwd
from member 
where member_no = ${member_no}

mapper.update:
UPDATE MEMBER SET MEMBER_NAME = #{MEMBER_NAME},
MEMBER_PASSWD = #{MEMBER_NEWPASSWD},
MEMBER_PHONE = #{MEMBER_PHONE}, MEMBER_ZIPCODE = #{MEMBER_ZIPCODE}, MEMBER_ADDR1 = #{MEMBER_ADDR1}, MEMBER_ADDR2 = #{MEMBER_ADDR2}, MEMBER_BIRTH = #{MEMBER_BIRTH}, EMAIL_AGREE = #{EMAIL_AGREE}, SMS_AGREE = #{SMS_AGREE} WHERE MEMBER_NO = #{MEMBER_NO}
